INSERT INTO organizations (`id`, `title_en`, `title_ar`, `slug_en`, `slug_ar`, `description_en`, `description_ar`, `logo`, `cover`, `rating`, `member_en`, `member_ar`, `phone`, `mobile`, `email`, `address_en`, `address_ar`, `facebook`, `twitter`, `instagram`, `youtube`, `active`, `is_top_5`, `parent_id`, `have_versions`, `can_show_in_home`, `content_count`, `created_at`, `updated_at`, `deleted_at`, `country_id`) VALUES 
('7','Holy Azhar','الأزهر الشريف','holy-azhar','الأزهر-الشريف','It was established more than 1,000 years ago, and it is a governmental, advocacy and global educational institution that guarantees general and university education and postgraduate studies in Sharia and Islamic sciences, Arabic language, languages ​​and applied and social sciences. It offers many scholarships to students all over the world and conducts research and studies and is affiliated to it (the Islamic Research Academy) whose activities extend in the continents of Asia, Europe, Africa and the Americas (and benefit from more than 60 countries in the world).','تم تأسيسيه منذ أكثر من 1000عام وهو مؤسسة حكومية دعوية تعليمية عالمية تكفل التعليم العام والجامعى والدراسات العليا فى العلوم الشرعية والاسلامية واللغة العربية واللغات والعلوم التطبيقية والاجتماعية ويقوم بتاهيل الدعاة وابتعاثهم ويتولى الافتاء وانشاء المراكز الاسلامية وادارتها ويقوم بكفالة طلاب العلم والمعلمين وتزويد المراكز الاسلامية بالائمة والدعاة والمعلمين ، يقدم العديد من المنح الدراسية للطلاب فى جميع انحاء العالم ويقوم بعمل البحوث والدرسات ويتبع له ( مجمع البحوث الاسلامية ) يمتد نشاطه فى قارات اسيا و اوربا وافريقيا و الامريكتين ( ويستفيد منه اكثر من 60 دولة فى العالم ).','1663068454logo.png','1663068454cover.png','','His Eminence the Greatest Imam, Professor Dr. Ahmed Al-Tayeb - the current Sheikh of Al-Azhar Al-Sharif','فضيلة الامام الاكبر الاستاذ الدكتور / احمد الطيب - شيخ الازهر الشريف الحالي','25900619','25900619','email@email.com','Cairo - Egypt','مصر - القاهرة','https://www.Facebook.com/','https://www.Twitter.com/','https://www.Instagram.com/','https://www.Youtube.com/','1','0','','0','1','11','2022-09-04 12:38:41','2022-10-02 10:37:12','2022-09-04 12:40:18','6');

INSERT INTO organizations (`id`, `title_en`, `title_ar`, `slug_en`, `slug_ar`, `description_en`, `description_ar`, `logo`, `cover`, `rating`, `member_en`, `member_ar`, `phone`, `mobile`, `email`, `address_en`, `address_ar`, `facebook`, `twitter`, `instagram`, `youtube`, `active`, `is_top_5`, `parent_id`, `have_versions`, `can_show_in_home`, `content_count`, `created_at`, `updated_at`, `deleted_at`, `country_id`) VALUES 
('8','General Secretariat of Awqaf','الأمانة العامة للأوقاف','general-secretariat-of-awqaf','الأمانة-العامة-للأوقاف','It is concerned with calling for the endowment and doing everything related to its affairs, including managing its money, investing it, and disbursing its proceeds within the limits of the terms of the endowment and in a manner that achieves the legitimate purposes of the endowment and the development of society civilly, culturally and socially.','تختص بالدعوة الى الوقف والقيام بكل مايتعلق بشؤنه بما فى ذلك ادارة امواله واستثمارها وصرف ريعها فى حدود شروط الوقف وبما يحقق المقاصد الشرعية للوقف وتنمية المجتمع حضاريا وثقافيا واجتماعيا .','1663069278logo.png','1663069278cover.png','','His Excellency Professor / Mohammed Abdullah Al-Jalahma','سعادة الاستاذ / محمد عبد الله الجلاهمة','670003/00965','1804777','email@email.com','Kuwait','الكويت','https://www.facebook.com/','https://www.Twitter.com/','https://www.Instagram.com/','https://www.Youtube.com/','1','0','','0','1','0','2022-09-04 12:38:41','2022-10-02 10:37:12','2022-09-04 12:40:18','7');

