INSERT INTO snippets (`id`, `title`, `name`, `desc_en`, `desc_ar`, `created_at`, `updated_at`, `deleted_at`) VALUES 
('1001','HomePage - Top Intro after slider','intro','<section class=\"bg-map\"> <div class=\"container pb-40\"> <div class=\"section-title text-center\"> <div class=\"row\"> <div class=\"col-md-8 col-md-offset-2\"><h2 class=\"text-uppercase line-bottom-center mt-0 mb-20\">The International Islamic Council </h2><h2 class=\"text-uppercase line-bottom-center mt-0 mb-20\"><span style=\"font-size: 16px;\">An international organization that means coordination between Islamic organizations in the world in the field of advocacy and relief, headed by Al-Azhar Al-Sharif</span> </h2></div></div></div> <div class=\"row multi-row-clearfix\"> <div class=\"col-xs-12 col-sm-4 col-md-4\"> <div class=\"aim-box\"> <div class=\"img-box\"><img src=\"/users/images/aim01.png\"></div><h3>25 years of giving and still giving</h3><div><br></div></div></div> <div class=\"col-xs-12 col-sm-4 col-md-4\"> <div class=\"aim-box\"> <div class=\"img-box\"><img src=\"/users/images/aim02.png\"></div><h3>Upgrading humanitarian work to be on the level of the message of Islam</h3><div><br></div></div></div> <div class=\"col-xs-12 col-sm-4 col-md-4\"> <div class=\"aim-box\"> <div class=\"img-box\"><img src=\"/users/images/aim03.png\"></div><h3>Medicine.accommodation. food</h3><h3>We Draw hope. Despite pain</h3><div><br></div></div></div></div> <div class=\"row text-center multi-row-clearfix\"><a href=\"/en/goals\" class=\"btn btn-lg btn-theme-colored pl-40 pr-40 mt-20\"> Goals</a></div></div></section>','<section class=\"bg-map\"> <div class=\"container pb-40\"> <div class=\"section-title text-center\"> <div class=\"row\"> <div class=\"col-md-8 col-md-offset-2\"><h2 class=\"text-uppercase line-bottom-center mt-0 mb-20\">المجلس الإسلامي <span class=\"text-theme-colored\">العالمي</span></h2><p>منظمة دولية تعني التنسيق بين المنظمات الاسلامية فى العالم فى مجال الدعوة والاغاثة برئاسة الازهر الشريف</p><p><br></p></div></div></div><div class=\"row multi-row-clearfix\"><div class=\"col-xs-12 col-sm-4 col-md-4\"><div class=\"aim-box\"><div class=\"img-box\"><img src=\"/users/images/aim01.png\"></div><h3>25 عاماً من العطاء ولازال العطاء مستمر</h3></div></div> <div class=\"col-xs-12 col-sm-4 col-md-4\"> <div class=\"aim-box\"> <div class=\"img-box\"><img src=\"/users/images/aim02.png\"></div><h3>الإرتقاء بالعمل الإنساني ليكون على مستوى رسالة الإسلام</h3></div></div> <div class=\"col-xs-12 col-sm-4 col-md-4\"> <div class=\"aim-box\"> <div class=\"img-box\"><img src=\"/users/images/aim03.png\"></div><h3>دواء ... إيواء ... غذاء <br> نرسم الأمل ... رغم الألم</h3></div></div></div> <div class=\"row text-center multi-row-clearfix\"><a href=\"/ar/%D8%A7%D9%84%D8%A3%D9%87%D8%AF%D8%A7%D9%81\" class=\"btn btn-lg btn-theme-colored pl-40 pr-40 mt-20\">كل أهداف المجلس</a></div></div></section>','2022-08-29 11:14:48','2022-09-15 11:35:00','');

INSERT INTO snippets (`id`, `title`, `name`, `desc_en`, `desc_ar`, `created_at`, `updated_at`, `deleted_at`) VALUES 
('1002','HomePage - Support charitable projects','support_charitable_causes','<div class=\"section-title text-right text-white pt-40\"><div class=\"row\"><div class=\"col-md-8\"><h2 class=\"mt-0 text-white\" style=\'font-family: \"Source Sans Pro\", -apple-system, BlinkMacSystemFont, \"Segoe UI\", Roboto, \"Helvetica Neue\", Arial, sans-serif, \"Apple Color Emoji\", \"Segoe UI Emoji\", \"Segoe UI Symbol\";\'><span style=\"font-family: inherit;\">Looking for a Charity to Support ?</span></h2><h2 class=\"mt-0 text-white\" style=\'font-family: \"Source Sans Pro\", -apple-system, BlinkMacSystemFont, \"Segoe UI\", Roboto, \"Helvetica Neue\", Arial, sans-serif, \"Apple Color Emoji\", \"Segoe UI Emoji\", \"Segoe UI Symbol\";\'><span style=\"font-size: 1rem; font-family: inherit;\">An international Islamic body that coordinates the activities of popular and official Islamic bodies and organizations in the world. It is chaired by the Grand Imam, Prof. Ahmed Al-Tayeb, Sheikh of Al-Azhar, with two deputies, His Excellency Dr. Abdullah Maatouq.</span><br></h2><div><br></div></div><div class=\"col-md-4\"></div></div></div>','<div class=\"section-title text-right text-white pt-40\"><div class=\"row\"><div class=\"col-md-8\"><h2 class=\"mt-0 text-white\" style=\'font-family: \"Source Sans Pro\", -apple-system, BlinkMacSystemFont, \"Segoe UI\", Roboto, \"Helvetica Neue\", Arial, sans-serif, \"Apple Color Emoji\", \"Segoe UI Emoji\", \"Segoe UI Symbol\";\'><span style=\"font-family: inherit; font-size: 16px;\"><b> </b></span><span style=\"font-family: inherit; font-size: 2rem;\">ادعم الأعمال الخيرية</span><br></h2><p>هيئة إسلامية عالمية تقوم بالتنسيق بين أنشطة الهيئات والمنظمات الإسلامية الشعبية والرسمية في العالم. ويرأسه فضيلة الإمام الأكبر أ.د, أحمد الطيب - شيخ الأزهر مع أثنين من نواب هم معالى الدكتور عبد الله معتوق</p></div><div class=\"col-md-4\"></div></div></div>','2022-08-29 11:14:48','2022-09-14 13:11:31','');

INSERT INTO snippets (`id`, `title`, `name`, `desc_en`, `desc_ar`, `created_at`, `updated_at`, `deleted_at`) VALUES 
('1003','HomePage -  Video popup text','video_tour','<h2 class=\"text-white text-uppercase font-38 font-weight-700 mb-20\"> Learn more about IICDR<h2 class=\"text-white text-uppercase font-38 font-weight-700 mb-20\"><br></h2><div><br></div></h2>','<h2 class=\"text-white text-uppercase font-38 font-weight-700 mb-20\">شاهد عن <span class=\"text-theme-colored\">المركز الإسلامي</span><p class=\"font-16 text-white\">هيئة إسلامية عالمية تقوم بالتنسيق بين أنشطة الهيئات والمنظمات الإسلامية الشعبية والرسمية في العالم. </p></h2>','2022-08-29 11:14:48','2022-09-14 13:14:27','');

