INSERT INTO snippets (`id`, `title`, `name`, `desc_en`, `desc_ar`, `created_at`, `updated_at`, `deleted_at`) VALUES 
('1006','About Us In Projects Page','about_us_in_projects','<div><span style=\"font-size: 1rem;\"> The idea of ​​establishing the International Islamic Council for Call and Relief came during the eleventh conference of the Islamic Research Academy in Cairo in 1988, under the auspices of His Excellency President / Mohamed Hosni Mubarak, as he stated in his speech to the meeting. The idea of ​​establishing the International Islamic Council for Call and Relief came during the eleventh conference of the Islamic Research Academy in Cairo in 1988, under the auspices of His Excellency President / Mohamed Hosni Mubarak, as he stated in his speech to the meeting.</span><br><div><br></div></div>','<p>جاءت فكرة إنشاء المجلس الإسلامي العالمي للدعوة والإغاثة ، خلال المؤتمر الحادي عشر لمجمع البحوث الإسلامية بالقاهرة عام 1988 م، تحت رعاية فخامة الرئيس / محمد حسني مبارك حيث جاء في كلمته إلى الإجتماع. جاءت فكرة إنشاء المجلس الإسلامي العالمي للدعوة والإغاثة ، خلال المؤتمر الحادي عشر لمجمع البحوث الإسلامية بالقاهرة عام 1988 م، تحت رعاية فخامة الرئيس / محمد حسني مبارك حيث جاء في كلمته إلى الإجتماع.</p>','2022-08-29 11:14:48','2022-09-14 13:23:31','');

