INSERT INTO snippets (`id`, `title`, `name`, `desc_en`, `desc_ar`, `created_at`, `updated_at`, `deleted_at`) VALUES 
('1009','Contact Us','contact','<div class=\"row\"><div class=\"col-md-12\"><p class=\"lead\"><span style=\"background-color: transparent; font-family: inherit; font-size: 28px; white-space: pre-wrap;\">The International Islamic Council for Call and Relief
</span><span style=\"background-color: transparent; font-family: inherit; font-size: 28px; white-space: pre-wrap;\">We are pleased and honored to Contact us  </span></p></div><div class=\"row\"><div class=\"col-xs-12 col-sm-6 col-md-6\"><div class=\"icon-box media border-1px bg-silver-light p-30 mb-20\"> <a class=\"media-left pull-left flip\" href=\"#\"> <i class=\"pe-7s-map-2 text-theme-colored\"></i></a><div class=\"media-body\"><h5 class=\"mt-0\">Address</h5><h5 class=\"mt-0\"><span style=\"font-size: 1rem;\">Council st, 10th District, Nasr city, Cairo, Egypt </span><br></h5></div></div></div><div class=\"col-xs-12 col-sm-6 col-md-6\"><div class=\"icon-box media border-1px bg-silver-light p-30 mb-20\"> <a class=\"media-left pull-left flip\" href=\"#\"> <i class=\"pe-7s-call text-theme-colored\"></i></a><div class=\"media-body text-right\"><h5 class=\"mt-0\">Phone Numbers </h5><h5 class=\"mt-0\"><span style=\"font-size: 1rem;\">0 2 23572100</span><br></h5><p class=\"ltr\">0 2 23572117</p></div></div></div></div><div class=\"row\"><div class=\"col-xs-12 col-sm-6 col-md-6\"><div class=\"icon-box media border-1px bg-silver-light p-30 mb-20\"> <a class=\"media-left pull-left flip\" href=\"#\"> <i class=\"pe-7s-mail text-theme-colored\"></i></a><div class=\"media-body\"><h5 class=\"mt-0\">Email</h5><h5 class=\"mt-0\"><span style=\"font-size: 1rem;\">info@iicdr.com</span><br></h5></div></div></div></div></div>','<div class=\"row\"><h3 class=\"line-bottom mt-0 mb-50 ml-15\">المجلس الإسلامي العالمي للدعوة والإغاثة</h3><div class=\"col-md-12\">                <p class=\"lead\"> يسعدنا ويشرفنا اتصالكم </p></div><div class=\"row\"><div class=\"col-xs-12 col-sm-6 col-md-6\"><div class=\"icon-box media border-1px bg-silver-light p-30 mb-20\"> <a class=\"media-left pull-left flip\" href=\"#\"> <i class=\"pe-7s-map-2 text-theme-colored\"></i></a><div class=\"media-body\"><h5 class=\"mt-0\">مقر المركز</h5><p>مصر - القاهرة - مدينة نصر - الحي العاشر - شارع المجلس</p></div></div></div><div class=\"col-xs-12 col-sm-6 col-md-6\"><div class=\"icon-box media border-1px bg-silver-light p-30 mb-20\"> <a class=\"media-left pull-left flip\" href=\"#\"> <i class=\"pe-7s-call text-theme-colored\"></i></a><div class=\"media-body text-right\"><h5 class=\"mt-0\">ارقام التواصل</h5><p class=\"ltr\">0 2 23572100 <br>0 2 23572117</p></div></div></div></div><div class=\"row\"><div class=\"col-xs-12 col-sm-6 col-md-6\"><div class=\"icon-box media border-1px bg-silver-light p-30 mb-20\"> <a class=\"media-left pull-left flip\" href=\"#\"> <i class=\"pe-7s-mail text-theme-colored\"></i></a><div class=\"media-body\"><h5 class=\"mt-0\">البريد الإلكتروني</h5><p>info@iicdr.com</p></div></div></div></div></div>','2022-09-06 11:07:12','2022-09-18 13:07:29','');

INSERT INTO snippets (`id`, `title`, `name`, `desc_en`, `desc_ar`, `created_at`, `updated_at`, `deleted_at`) VALUES 
('1010','committese Description','committese_description','<div> An international Islamic body that coordinates the activities of popular and official Islamic bodies and organizations in the world. It is chaired by the Grand Imam, Prof. Ahmed Al-Tayeb, Sheikh of Al-Azhar, with two deputies: His Excellency Dr. Abdullah Matouk Al-Maatouq and His Excellency Dr. Abdul Rahman bin Abdullah Al-Zaid, and the Secretary-General of the Council His Excellency Dr. Abdullah Al-Musleh. The Presidency includes the heads of the specialized committees.<div><br></div></div>','<p>هيئة إسلامية عالمية تقوم بالتنسيق بين أنشطة الهيئات والمنظمات الإسلامية الشعبية والرسمية في العالم. ويرأسه فضيلة  الإمام الأكبر أ.د,  أحمد الطيب - شيخ الأزهر مع أثنين من  نواب هم معالى الدكتور عبد الله معتوق المعتوق و معالي الدكتور عبد الرحمن بن عبد الله الزيد، والأمين العام للمجلس معالي الدكتور عبد الله المصلح، وتضم هيئة الرئاسة رؤساء اللجان المتخصصة.</p>','2022-09-07 15:44:46','2022-09-14 13:29:04','');

