INSERT INTO snippets (`id`, `title`, `name`, `desc_en`, `desc_ar`, `created_at`, `updated_at`, `deleted_at`) VALUES 
('1012','aphorisms','aphorisms','<div class=\"tw-ta-container F0azHf tw-nfl\" id=\"tw-target-text-container\" tabindex=\"0\" style=\"overflow: hidden; position: relative; outline: 0px;\"><pre class=\"tw-data-text tw-text-large tw-ta\" data-placeholder=\"Translation\" id=\"tw-target-text\" dir=\"ltr\" style=\"unicode-bidi: isolate; line-height: 36px; background-color: transparent; border: none; padding: 2px 0.14em 2px 0px; position: relative; margin-top: -2px; margin-bottom: -2px; resize: none; overflow: hidden; width: 270px; overflow-wrap: break-word;\"><pre class=\"tw-data-text tw-text-large tw-ta\" data-placeholder=\"Translation\" id=\"tw-target-text\" dir=\"ltr\" style=\"margin-top: -2px; margin-bottom: -2px; overflow: hidden; padding: 2px 0.14em 2px 0px; background-color: transparent; unicode-bidi: isolate; line-height: 36px; border: none; position: relative; resize: none; width: 270px; overflow-wrap: break-word;\"><span style=\"font-size: 28px; white-space: pre-wrap;\"><font face=\"Source Sans Pro, -apple-system, BlinkMacSystemFont, Segoe UI, Roboto, Helvetica Neue, Arial, sans-serif, Apple Color Emoji, Segoe UI Emoji, Segoe UI Symbol\"> </font></span></pre><div class=\"tw-ta-container F0azHf tw-nfl\" id=\"tw-target-text-container\" tabindex=\"0\" style=\"overflow: hidden; position: relative; outline: 0px;\"><pre class=\"tw-data-text tw-text-large tw-ta\" data-placeholder=\"Translation\" id=\"tw-target-text\" dir=\"ltr\" style=\"unicode-bidi: isolate; font-size: 28px; line-height: 36px; background-color: transparent; border: none; padding: 2px 0.14em 2px 0px; position: relative; margin-top: -2px; margin-bottom: -2px; resize: none; font-family: inherit; overflow: hidden; width: 270px; white-space: pre-wrap; overflow-wrap: break-word;\"><span class=\"Y2IQFc\" lang=\"en\">Aphorisms</span><span style=\"white-space: normal; background-color: transparent; font-style: italic; font-size: 14px;\">  </span><span style=\"font-family: inherit; font-size: 1.75rem; background-color: transparent; font-style: italic;\">It is not a coincidence that the name of this council combines “relief” and “dawa” and perhaps this was intended at the origin of its name.</span></pre></div><h3 class=\"line-bottom border-bottom mt-0\" style=\'font-family: \"Source Sans Pro\", -apple-system, BlinkMacSystemFont, \"Segoe UI\", Roboto, \"Helvetica Neue\", Arial, sans-serif, \"Apple Color Emoji\", \"Segoe UI Emoji\", \"Segoe UI Symbol\"; color: rgb(33, 37, 41); white-space: normal;\'><span class=\"fa fa-quote-left text-theme-colored ml-10\" style=\"background-color: transparent; font-size: 14px;\"></span><br></h3><p class=\"font-italic mb-10\"><span class=\"fa fa-quote-left text-theme-colored mr-10\"></span></p><img src=\"http://iicdr.kahataine.org/public/images/team/sm1.jpg\" alt=\"\" class=\"mb-15 mt-20 round-small\"><h4 class=\"mt-0 mb-0 text-theme-colored\" style=\'font-family: \"Source Sans Pro\", -apple-system, BlinkMacSystemFont, \"Segoe UI\", Roboto, \"Helvetica Neue\", Arial, sans-serif, \"Apple Color Emoji\", \"Segoe UI Emoji\", \"Segoe UI Symbol\"; color: rgb(33, 37, 41); white-space: normal;\'><b>Dr.Ahmed El - Tayb </b></h4><p class=\"text-gray-darkgray mt-0 mb-20\" style=\'font-family: \"Source Sans Pro\", -apple-system, BlinkMacSystemFont, \"Segoe UI\", Roboto, \"Helvetica Neue\", Arial, sans-serif, \"Apple Color Emoji\", \"Segoe UI Emoji\", \"Segoe UI Symbol\"; font-size: 16px; white-space: normal;\'> President of the International Islamic Council for Call and Relief</p></pre></div>','<h3 class=\"line-bottom border-bottom mt-0\">كلمات مأثورة<p class=\"font-italic mb-10\"><i class=\"fa fa-quote-left text-theme-colored ml-10\"></i> ليس صدفة ان يجمع اسم هذا المجلس بين \"الإغاثة\" و \"الدعوة\" و لعل هذا كان مقصود في أصل تسميته <i class=\"fa fa-quote-left text-theme-colored mr-10\"></i></p><img src=\"http://iicdr.kahataine.org/public/images/team/sm1.jpg\" alt=\"\" class=\"mb-15 mt-20 round-small\"><h4 class=\"mt-0 mb-0 text-theme-colored\">دكتور أحمد الطيب</h4><p class=\"text-gray-darkgray mt-0 mb-20\">رئيس المجلس الإسلامي العالمي للدعوة و الإغاثة</p></h3>','2022-09-11 13:03:37','2022-09-19 08:47:45','');

