INSERT INTO tesimonials (`id`, `author_en`, `author_ar`, `position_en`, `position_ar`, `content_en`, `content_ar`, `photo`, `display_order`, `active`, `created_at`, `updated_at`, `deleted_at`) VALUES 
('1','د. عبد الله بن عبد العزيز المصلح','د. عبد الله بن عبد العزيز المصلح','الأمين العام للمجلس','الأمين العام للمجلس','<p>هيئة إسلامية عالمية تقوم بالتنسيق بين أنشطة الهيئات والمنظمات الإسلامية الشعبية والرسمية في العالم. ويرأسه فضيلة الإمام الأكبر أ.د, أحمد الطيب - شيخ الأزهر مع أثنين من نواب هم معالى الدكتور عبد الله معتوق المعتوق و معالي الدكتور عبد الرحمن بن عبد الله الزيد، والأمين العام للمجلس معالي الدكتور عبد الله المصلح، وتضم هيئة الرئاسة رؤساء اللجان المتخصصة.</p>','<p>هيئة إسلامية عالمية تقوم بالتنسيق بين أنشطة الهيئات والمنظمات الإسلامية الشعبية والرسمية في العالم. ويرأسه فضيلة الإمام الأكبر أ.د, أحمد الطيب - شيخ الأزهر مع أثنين من نواب هم معالى الدكتور عبد الله معتوق المعتوق و معالي الدكتور عبد الرحمن بن عبد الله الزيد، والأمين العام للمجلس معالي الدكتور عبد الله المصلح، وتضم هيئة الرئاسة رؤساء اللجان المتخصصة.</p>','1661938626.png','','1','2022-08-31 09:35:22','2022-08-31 09:37:15','2022-08-31 09:37:15');

